import { useState, useEffect } from 'react';
import { useQuery, useMutation } from '@tanstack/react-query';
import { ArrowLeft, TrendingUp, Clock, Zap, Calendar } from 'lucide-react';
import { Link } from 'wouter';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { Progress } from '@/components/ui/progress';
import Header from '@/components/Header';
import Footer from '@/components/Footer';
import NumberSelector from '@/components/NumberSelector';
import StakeInput from '@/components/StakeInput';
import WinningsCalculator from '@/components/WinningsCalculator';
import PrintTicket from '@/components/PrintTicket';
import { useToast } from '@/hooks/use-toast';
import { apiRequest, queryClient } from '@/lib/queryClient';
import type { Game, Bet, Result } from '@shared/schema';

export default function VirtualBetting() {
  const { toast } = useToast();
  const [selectedNumbers, setSelectedNumbers] = useState<number[]>([]);
  const [stakeAmount, setStakeAmount] = useState(1000);
  const [currentBetId, setCurrentBetId] = useState<string | null>(null);
  const [generatedResult, setGeneratedResult] = useState<Result | null>(null);
  const [betOutcome, setBetOutcome] = useState<any | null>(null);
  const [currentWeek, setCurrentWeek] = useState(1);
  const [timeUntilNextWeek, setTimeUntilNextWeek] = useState('');

  const { data: game } = useQuery<Game>({
    queryKey: ['/api/games', 'virtual'],
    queryFn: async () => {
      const response = await fetch('/api/games/virtual');
      if (!response.ok) throw new Error('Game not found');
      return await response.json() as Game;
    },
  });

  // Calculate current week (1-35 cycle) and time until next week
  useEffect(() => {
    const calculateWeekAndTime = () => {
      const now = new Date();
      const startDate = new Date('2025-01-01'); // Start of the year
      const daysSinceStart = Math.floor((now.getTime() - startDate.getTime()) / (1000 * 60 * 60 * 24));
      const weeksSinceStart = Math.floor(daysSinceStart / 7);
      const week = (weeksSinceStart % 35) + 1; // 35-week cycle

      setCurrentWeek(week);

      // Calculate time until next week (next Sunday at 6 PM)
      const nextWeek = new Date(now);

      // Calculate days until next Sunday (0 if already Sunday)
      const daysUntilSunday = now.getDay() === 0 ? 0 : 7 - now.getDay();
      nextWeek.setDate(now.getDate() + daysUntilSunday);
      nextWeek.setHours(18, 0, 0, 0);

      // If the target is in the past (including Sunday after 6 PM), move to next week
      if (nextWeek.getTime() <= now.getTime()) {
        nextWeek.setDate(nextWeek.getDate() + 7);
      }

      const diff = nextWeek.getTime() - now.getTime();
      const days = Math.floor(diff / (1000 * 60 * 60 * 24));
      const hours = Math.floor((diff % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60));
      const minutes = Math.floor((diff % (1000 * 60 * 60)) / (1000 * 60));
      const seconds = Math.floor((diff % (1000 * 60)) / 1000);

      return `${days}d ${hours.toString().padStart(2, '0')}h ${minutes.toString().padStart(2, '0')}m ${seconds.toString().padStart(2, '0')}s`;
    };

    const timer = setInterval(() => {
      setTimeUntilNextWeek(calculateWeekAndTime());
    }, 1000);

    setTimeUntilNextWeek(calculateWeekAndTime());

    return () => clearInterval(timer);
  }, []);

  const placeBetMutation = useMutation({
    mutationFn: async () => {
      if (!game) throw new Error('Game not loaded');

      const betData = {
        gameId: game.id,
        selectedNumbers,
        bonusNumber: undefined,
        stakeAmount: stakeAmount,
      };

      return await apiRequest('POST', '/api/bets', betData);
    },
    onSuccess: async (response) => {
      const betResponse = await response.json();
      setCurrentBetId(betResponse.id);
      queryClient.invalidateQueries({ queryKey: ['/api/bets'] });

      toast({
        title: 'Bet Placed for Virtual Week ' + currentWeek,
        description: `Your bet will be drawn on Sunday at 6 PM.`,
      });

      // For demo purposes, generate instant result
      if (game) {
        setTimeout(() => {
          generateResultMutation.mutate(game.id);
        }, 2000);
      }
    },
    onError: (error: any) => {
      console.error('Bet placement error:', error);
      const errorMessage = error?.message || 'Please try again.';
      toast({
        title: 'Failed to Place Bet',
        description: errorMessage,
        variant: 'destructive',
      });
    },
  });

  const generateResultMutation = useMutation({
    mutationFn: async (gameId: string) => {
      const response = await fetch(`/api/results/generate/${gameId}`, {
        method: 'POST',
      });
      if (!response.ok) throw new Error('Failed to generate result');
      return await response.json() as Result;
    },
    onSuccess: async (result) => {
      setGeneratedResult(result);
      queryClient.invalidateQueries({ queryKey: ['/api/results'] });
      queryClient.invalidateQueries({ queryKey: ['/api/winnings'] });

      if (currentBetId) {
        try {
          const checkResponse = await fetch(`/api/winnings/check/${currentBetId}`, {
            method: 'POST',
          });
          const outcomeData = await checkResponse.json();
          setBetOutcome(outcomeData);

          queryClient.invalidateQueries({ queryKey: ['/api/bets', currentBetId, 'ticket'] });
          queryClient.invalidateQueries({ queryKey: ['/api/bets', currentBetId] });

          if (outcomeData.won) {
            toast({
              title: '🎉 Virtual Week ' + currentWeek + ' Winner!',
              description: outcomeData.message || `You won ₦${outcomeData.winning.winningAmount.toLocaleString()}!`,
            });
          } else {
            toast({
              title: 'Better Luck Next Week',
              description: outcomeData.message || 'No winning numbers matched',
              variant: 'destructive',
            });
          }
        } catch (error) {
          console.error('Failed to check outcome:', error);
        }
      }

      const winningNums = JSON.parse(result.winningNumbers) as number[];
      toast({
        title: 'Virtual Week ' + currentWeek + ' Draw Complete!',
        description: `Winning numbers: ${winningNums.join(', ')}`,
      });
    },
  });

  const handlePlaceBet = () => {
    if (!game) return;

    if (selectedNumbers.length !== game.numbersToSelect) {
      toast({
        title: 'Invalid Selection',
        description: `Please select exactly ${game.numbersToSelect} numbers.`,
        variant: 'destructive',
      });
      return;
    }

    placeBetMutation.mutate();
  };

  const handleReset = () => {
    setSelectedNumbers([]);
    setCurrentBetId(null);
    setGeneratedResult(null);
    setBetOutcome(null);
  };

  if (!game) {
    return (
      <div className="min-h-screen bg-gradient-to-b from-gray-50 to-gray-100 dark:from-gray-900 dark:to-gray-950">
        <Header />
        <div className="container mx-auto px-4 py-8">
          <div className="text-center">
            <p className="text-gray-600 dark:text-gray-400">Loading game...</p>
          </div>
        </div>
        <Footer />
      </div>
    );
  }

  const weekProgress = (currentWeek / 35) * 100;

  return (
    <div className="min-h-screen bg-gradient-to-b from-gray-50 to-gray-100 dark:from-gray-900 dark:to-gray-950">
      <Header />

      <div className="container mx-auto px-4 py-8">
        <div className="mb-6">
          <Link href="/#games">
            <Button variant="ghost" className="gap-2" data-testid="button-back">
              <ArrowLeft className="h-4 w-4" />
              Back to Games
            </Button>
          </Link>
        </div>

        <div className="max-w-4xl mx-auto">
          <Card className="mb-6 bg-gradient-to-r from-green-600 to-emerald-700 text-white border-0">
            <CardHeader>
              <div className="flex items-center justify-between">
                <div>
                  <div className="flex items-center gap-3 mb-2">
                    <TrendingUp className="h-10 w-10" />
                    <CardTitle className="text-3xl font-bold">{game.name}</CardTitle>
                  </div>
                  <CardDescription className="text-green-100 text-lg">
                    {game.description}
                  </CardDescription>
                </div>
                <div className="text-center">
                  <div className="bg-white/20 backdrop-blur rounded-full w-24 h-24 flex flex-col items-center justify-center mb-2">
                    <Calendar className="h-8 w-8 mb-1" />
                    <span className="text-3xl font-bold">{currentWeek}</span>
                  </div>
                  <Badge className="bg-white text-green-600 font-semibold">
                    Week {currentWeek}/35
                  </Badge>
                </div>
              </div>
            </CardHeader>
            <CardContent>
              <div className="space-y-4">
                <div className="bg-white/20 backdrop-blur rounded-lg p-4">
                  <div className="flex items-center justify-between mb-2">
                    <div>
                      <p className="text-green-100 text-sm mb-1">Next Draw In</p>
                      <p className="text-2xl font-mono font-bold">{timeUntilNextWeek}</p>
                      <p className="text-green-100 text-xs mt-1">Sunday at 6:00 PM</p>
                    </div>
                    <div className="text-right">
                      <p className="text-green-100 text-sm mb-1">Odds Range</p>
                      <p className="text-2xl font-bold">{game.minOdds}x - {game.maxOdds}x</p>
                    </div>
                  </div>
                </div>

                <div>
                  <div className="flex items-center justify-between mb-2">
                    <span className="text-green-100 text-sm">35-Week Cycle Progress</span>
                    <span className="text-white font-semibold">{Math.round(weekProgress)}%</span>
                  </div>
                  <Progress value={weekProgress} className="h-3 bg-white/30" />
                  <p className="text-green-100 text-xs mt-1">
                    {35 - currentWeek} weeks remaining in current cycle
                  </p>
                </div>
              </div>
            </CardContent>
          </Card>

          {!currentBetId && !generatedResult ? (
            <div className="grid gap-6">
              <Card className="bg-white dark:bg-gray-800">
                <CardHeader>
                  <CardTitle className="text-gray-900 dark:text-white">Select Your Numbers</CardTitle>
                  <CardDescription className="text-gray-600 dark:text-gray-400">
                    Pick {game.numbersToSelect} numbers from {game.minNumber} to {game.maxNumber} for Week {currentWeek}
                  </CardDescription>
                </CardHeader>
                <CardContent>
                  <NumberSelector
                    minNumber={game.minNumber}
                    maxNumber={game.maxNumber}
                    maxSelections={game.numbersToSelect}
                    selectedNumbers={selectedNumbers}
                    onSelectionChange={(numbers) => setSelectedNumbers(numbers)}
                    hasBonus={false}
                  />
                </CardContent>
              </Card>

              <div className="grid md:grid-cols-2 gap-6">
                <StakeInput
                  initialStake={stakeAmount}
                  onStakeChange={setStakeAmount}
                />
                <WinningsCalculator
                  selectedCount={selectedNumbers.length}
                  requiredMatches={game.minMatches}
                  stakeAmount={stakeAmount}
                  minOdds={game.minOdds}
                  maxOdds={game.maxOdds}
                />
              </div>

              <Button
                onClick={handlePlaceBet}
                disabled={placeBetMutation.isPending || selectedNumbers.length !== game.numbersToSelect}
                className="w-full bg-green-600 hover:bg-green-700 dark:bg-green-600 dark:hover:bg-green-700 text-white py-6 text-lg font-semibold"
                data-testid="button-place-bet"
              >
                <Zap className="mr-2 h-5 w-5" />
                {placeBetMutation.isPending ? 'Placing Bet...' : `Place Virtual Bet - Week ${currentWeek} - ₦${stakeAmount.toLocaleString()}`}
              </Button>

              <div className="bg-blue-50 dark:bg-blue-950 border border-blue-200 dark:border-blue-800 rounded-lg p-4">
                <div className="flex items-start gap-3">
                  <Clock className="h-5 w-5 text-blue-600 dark:text-blue-400 mt-0.5" />
                  <div>
                    <h4 className="font-semibold text-blue-900 dark:text-blue-100 mb-1">How Virtual Betting Works</h4>
                    <ul className="text-sm text-blue-800 dark:text-blue-200 space-y-1">
                      <li>• 35 weeks per cycle, currently in Week {currentWeek}</li>
                      <li>• New draw every Sunday at 6:00 PM</li>
                      <li>• Match {game.minMatches}+ numbers to win</li>
                      <li>• Instant results after each weekly draw</li>
                    </ul>
                  </div>
                </div>
              </div>
            </div>
          ) : (
            <div className="space-y-6">
              {currentBetId && !generatedResult && (
                <Card className="bg-blue-50 dark:bg-blue-950 border-2 border-blue-500">
                  <CardHeader>
                    <CardTitle className="text-blue-900 dark:text-blue-100">✓ Bet Placed Successfully!</CardTitle>
                    <CardDescription className="text-blue-700 dark:text-blue-300">
                      Your bet for Week {currentWeek} has been recorded. Print your betting slip below.
                    </CardDescription>
                  </CardHeader>
                  <CardContent>
                    <PrintTicket betId={currentBetId} autoOpen={false} />
                    <p className="text-sm text-blue-600 dark:text-blue-400 mt-4 text-center">
                      ⏳ Results will be generated in a moment...
                    </p>
                  </CardContent>
                </Card>
              )}

              {currentBetId && generatedResult && (
                <Card className="bg-gray-50 dark:bg-gray-900">
                  <CardHeader>
                    <CardTitle className="text-gray-900 dark:text-gray-100">Print Results Ticket</CardTitle>
                    <CardDescription className="text-gray-600 dark:text-gray-400">
                      Your official results ticket with winning numbers
                    </CardDescription>
                  </CardHeader>
                  <CardContent>
                    <PrintTicket betId={currentBetId} autoOpen={false} />
                  </CardContent>
                </Card>
              )}

              {generatedResult && betOutcome && (
                <Card className={betOutcome.won ? 'border-2 border-green-500 bg-green-50 dark:bg-green-950' : 'bg-white dark:bg-gray-800'}>
                  <CardHeader>
                    <CardTitle className={betOutcome.won ? 'text-green-700 dark:text-green-400' : 'text-gray-900 dark:text-white'}>
                      {betOutcome.won ? `🎉 Week ${currentWeek} Winner!` : 'Better Luck Next Week'}
                    </CardTitle>
                  </CardHeader>
                  <CardContent>
                    <div className="space-y-4">
                      <div>
                        <p className="text-sm text-gray-600 dark:text-gray-400 mb-2">Week {currentWeek} Winning Numbers:</p>
                        <div className="flex flex-wrap gap-2">
                          {JSON.parse(generatedResult.winningNumbers).map((num: number, idx: number) => (
                            <div key={idx} className="w-12 h-12 rounded-full bg-green-100 dark:bg-green-900 flex items-center justify-center font-bold text-green-700 dark:text-green-300">
                              {num}
                            </div>
                          ))}
                        </div>
                      </div>
                      {betOutcome.won && (
                        <div className="bg-green-100 dark:bg-green-900 p-4 rounded-lg">
                          <p className="text-green-800 dark:text-green-200 font-semibold text-lg">
                            You won: ₦{betOutcome.winning.winningAmount.toLocaleString()}
                          </p>
                          <p className="text-green-700 dark:text-green-300 text-sm mt-1">
                            {betOutcome.message}
                          </p>
                        </div>
                      )}
                    </div>
                  </CardContent>
                </Card>
              )}

              <Button
                onClick={handleReset}
                className="w-full bg-gray-600 hover:bg-gray-700 dark:bg-gray-600 dark:hover:bg-gray-700"
                data-testid="button-play-again"
              >
                Play Another Week
              </Button>
            </div>
          )}
        </div>
      </div>

      <Footer />
    </div>
  );
}

client/src/components/LoadBetDialog.tsx.content:
import { useState } from 'react';
import { Search, Ticket } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Card } from '@/components/ui/card';
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from '@/components/ui/dialog';
import PrintTicket from '@/components/PrintTicket';
import { useToast } from '@/hooks/use-toast';

export default function LoadBetDialog() {
  const [open, setOpen] = useState(false);
  const [betCode, setBetCode] = useState('');
  const [loading, setLoading] = useState(false);
  const [ticket, setTicket] = useState<any | null>(null);
  const { toast } = useToast();

  const handleLoadBet = async () => {
    if (!betCode.trim()) {
      toast({
        title: 'Enter Bet Code',
        description: 'Please enter a bet ID or short code',
        variant: 'destructive',
      });
      return;
    }

    setLoading(true);
    try {
      let betId = betCode.trim();

      // If it's a short code (8 characters), search for the full bet ID first
      if (betId.length === 8) {
        const searchResponse = await fetch(`/api/bets/search/${betId}`);

        if (!searchResponse.ok) {
          throw new Error('Bet not found with that short code');
        }

        const searchData = await searchResponse.json();
        betId = searchData.betId;
      }

      // Now fetch the full ticket using the bet ID
      const response = await fetch(`/api/bets/${betId}/ticket`);

      if (!response.ok) {
        throw new Error('Bet not found');
      }

      const ticketData = await response.json();
      setTicket(ticketData);

      toast({
        title: 'Bet Found!',
        description: `Loaded ticket for ${ticketData.gameName}`,
      });
    } catch (error) {
      toast({
        title: 'Bet Not Found',
        description: 'No bet found with that ID or code. Please check and try again.',
        variant: 'destructive',
      });
      setTicket(null);
    } finally {
      setLoading(false);
    }
  };

  const handleClose = () => {
    setOpen(false);
    setBetCode('');
    setTicket(null);
  };

  return (
    <Dialog open={open} onOpenChange={setOpen}>
      <DialogTrigger asChild>
        <Button variant="outline" className="gap-2" data-testid="button-load-bet">
          <Ticket className="h-4 w-4" />
          Load Bet
        </Button>
      </DialogTrigger>
      <DialogContent className="max-w-2xl">
        <DialogHeader>
          <DialogTitle>Load Bet Ticket</DialogTitle>
          <DialogDescription>
            Enter your bet ID or short code to retrieve your ticket
          </DialogDescription>
        </DialogHeader>

        <div className="space-y-4">
          {/* Search Input */}
          <div className="flex gap-2">
            <Input
              placeholder="Enter Bet ID or Short Code (e.g., 1A2B3C4D)"
              value={betCode}
              onChange={(e) => setBetCode(e.target.value.toUpperCase())}
              onKeyDown={(e) => e.key === 'Enter' && handleLoadBet()}
              disabled={loading}
              className="font-mono"
              data-testid="input-bet-code"
            />
            <Button 
              onClick={handleLoadBet} 
              disabled={loading}
              data-testid="button-search-bet"
            >
              <Search className="h-4 w-4 mr-2" />
              {loading ? 'Searching...' : 'Search'}
            </Button>
          </div>

          {/* Display Ticket if Found */}
          {ticket && (
            <Card className="p-6">
              <div className="space-y-4">
                {/* Header */}
                <div className="text-center border-b-2 border-dashed pb-4">
                  <h2 className="font-display font-bold text-2xl">AfriBet Games</h2>
                  <p className="text-sm text-muted-foreground">Bet Ticket</p>
                </div>

                {/* Ticket Details */}
                <div className="space-y-3 text-sm">
                  <div className="bg-muted/50 p-3 rounded">
                    <div className="flex justify-between mb-1">
                      <span className="text-muted-foreground font-medium">Ticket ID:</span>
                      <span className="font-mono font-bold">{ticket.ticketId}</span>
                    </div>
                    <div className="flex justify-between">
                      <span className="text-muted-foreground">Short Code:</span>
                      <span className="font-mono font-semibold">{ticket.ticketId.slice(0, 8).toUpperCase()}</span>
                    </div>
                  </div>

                  <div className="flex justify-between py-2 border-b">
                    <span className="text-muted-foreground font-medium">Game:</span>
                    <span className="font-bold">{ticket.gameName}</span>
                  </div>

                  <div className="flex justify-between py-2 border-b">
                    <span className="text-muted-foreground font-medium">Date:</span>
                    <span className="font-semibold">{new Date(ticket.createdAt).toLocaleString()}</span>
                  </div>

                  {/* Selected Numbers */}
                  <div className="border-t-2 border-dashed pt-3">
                    <p className="text-muted-foreground font-semibold mb-2">Your Numbers:</p>
                    <div className="flex flex-wrap gap-2 justify-center">
                      {ticket.selectedNumbers.map((num: number, idx: number) => (
                        <span key={idx} className="w-10 h-10 rounded-full bg-primary/20 flex items-center justify-center font-bold text-lg">
                          {num}
                        </span>
                      ))}
                      {ticket.bonusNumber && (
                        <>
                          <span className="flex items-center text-muted-foreground font-bold">+</span>
                          <span className="w-10 h-10 rounded-full bg-accent/20 flex items-center justify-center font-bold text-lg">
                            {ticket.bonusNumber}
                          </span>
                        </>
                      )}
                    </div>
                  </div>

                  {/* Financial Info */}
                  <div className="bg-muted/50 p-3 rounded space-y-2">
                    <div className="flex justify-between">
                      <span className="text-muted-foreground font-medium">Stake:</span>
                      <span className="font-bold text-lg">₦{ticket.stakeAmount.toLocaleString()}</span>
                    </div>
                    {ticket.potentialWinning && (
                      <div className="flex justify-between text-xs">
                        <span className="text-muted-foreground">Potential Win:</span>
                        <span className="font-semibold">₦{ticket.potentialWinning.toLocaleString()}</span>
                      </div>
                    )}
                  </div>

                  {/* Status */}
                  {ticket.status === 'won' && ticket.winning && (
                    <div className="bg-green-500/20 rounded p-4 border-2 border-green-500">
                      <div className="text-center space-y-2">
                        <p className="font-bold text-xl text-green-700 dark:text-green-400">🎉 WINNER! 🎉</p>
                        <div className="flex justify-between items-center">
                          <span className="font-semibold text-green-700 dark:text-green-400">Payout:</span>
                          <span className="font-bold text-2xl text-green-700 dark:text-green-400">₦{ticket.winning.winningAmount.toLocaleString()}</span>
                        </div>
                      </div>
                    </div>
                  )}

                  {ticket.status === 'lost' && (
                    <div className="bg-red-500/20 rounded p-3 border-2 border-red-500">
                      <p className="text-center font-bold text-red-700 dark:text-red-400">Not a Winner</p>
                    </div>
                  )}

                  {ticket.status === 'pending' && (
                    <div className="bg-yellow-500/20 rounded p-3 border-2 border-yellow-500">
                      <p className="text-center font-semibold text-yellow-700 dark:text-yellow-400">Results Pending</p>
                    </div>
                  )}

                  {/* Winning Numbers */}
                  {ticket.result && (
                    <div className="border-t-2 border-dashed pt-3">
                      <p className="text-muted-foreground font-semibold mb-2">Winning Numbers:</p>
                      <div className="flex flex-wrap gap-2 justify-center">
                        {ticket.result.winningNumbers.map((num: number, idx: number) => (
                          <span key={idx} className="w-10 h-10 rounded-full bg-muted flex items-center justify-center font-bold text-lg">
                            {num}
                          </span>
                        ))}
                      </div>
                    </div>
                  )}
                </div>

                {/* Actions */}
                <div className="flex gap-2 pt-4 border-t">
                  <PrintTicket betId={ticket.ticketId} />
                  <Button variant="outline" onClick={handleClose} className="flex-1">
                    Close
                  </Button>
                </div>
              </div>
            </Card>
          )}

          {/* Help Text */}
          {!ticket && (
            <div className="text-center text-sm text-muted-foreground bg-muted/30 p-4 rounded">
              <p className="font-medium mb-2">How to find your bet code:</p>
              <ul className="text-xs space-y-1">
                <li>• Check your printed ticket for the Short Code</li>
                <li>• Look in your Dashboard for recent bets</li>
                <li>• The code is shown after placing a bet</li>
              </ul>
            </div>
          )}
        </div>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/ContactModal.tsx.content:
import { useState, useEffect } from 'react';
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle } from '@/components/ui/dialog';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Textarea } from '@/components/ui/textarea';
import { Label } from '@/components/ui/label';
import { useToast } from '@/hooks/use-toast';
import { Mail, Phone, MapPin, Send } from 'lucide-react';

interface ContactModalProps {
  open: boolean;
  onOpenChange: (open: boolean) => void;
  type?: 'general' | 'investor';
}

export default function ContactModal({ open, onOpenChange, type = 'general' }: ContactModalProps) {
  const { toast } = useToast();
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    subject: '',
    message: ''
  });
  const [isSubmitting, setIsSubmitting] = useState(false);

  // Reset form and set subject when modal opens or type changes
  useEffect(() => {
    if (open) {
      setFormData({
        name: '',
        email: '',
        phone: '',
        subject: type === 'investor' ? 'Investment Inquiry' : '',
        message: ''
      });
    }
  }, [open, type]);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setIsSubmitting(true);

    // Simulate form submission
    setTimeout(() => {
      toast({
        title: "Message Sent Successfully!",
        description: "We'll get back to you within 24 hours.",
      });
      setFormData({ name: '', email: '', phone: '', subject: '', message: '' });
      setIsSubmitting(false);
      onOpenChange(false);
    }, 1500);
  };

  return (
    <Dialog open={open} onOpenChange={onOpenChange}>
      <DialogContent className="sm:max-w-[550px] max-h-[90vh] overflow-y-auto">
        <DialogHeader>
          <DialogTitle className="text-2xl font-display">
            {type === 'investor' ? 'Investment Inquiry' : 'Contact Us'}
          </DialogTitle>
          <DialogDescription>
            {type === 'investor' 
              ? 'Fill out the form below and we\'ll send you our investment proposal.'
              : 'Get in touch with us. We\'d love to hear from you!'}
          </DialogDescription>
        </DialogHeader>

        <form onSubmit={handleSubmit} className="space-y-4 mt-4">
          <div className="space-y-2">
            <Label htmlFor="name">Full Name *</Label>
            <Input
              id="name"
              value={formData.name}
              onChange={(e) => setFormData({ ...formData, name: e.target.value })}
              required
              placeholder="John Doe"
              data-testid="input-contact-name"
            />
          </div>

          <div className="space-y-2">
            <Label htmlFor="email">Email Address *</Label>
            <Input
              id="email"
              type="email"
              value={formData.email}
              onChange={(e) => setFormData({ ...formData, email: e.target.value })}
              required
              placeholder="john@example.com"
              data-testid="input-contact-email"
            />
          </div>

          <div className="space-y-2">
            <Label htmlFor="phone">Phone Number</Label>
            <Input
              id="phone"
              type="tel"
              value={formData.phone}
              onChange={(e) => setFormData({ ...formData, phone: e.target.value })}
              placeholder="+234 800 000 0000"
              data-testid="input-contact-phone"
            />
          </div>

          {type !== 'investor' && (
            <div className="space-y-2">
              <Label htmlFor="subject">Subject *</Label>
              <Input
                id="subject"
                value={formData.subject}
                onChange={(e) => setFormData({ ...formData, subject: e.target.value })}
                required
                placeholder="What is this regarding?"
                data-testid="input-contact-subject"
              />
            </div>
          )}

          <div className="space-y-2">
            <Label htmlFor="message">Message *</Label>
            <Textarea
              id="message"
              value={formData.message}
              onChange={(e) => setFormData({ ...formData, message: e.target.value })}
              required
              placeholder={type === 'investor' 
                ? "Tell us about your investment interest and we'll send you our detailed proposal..."
                : "Your message here..."}
              rows={5}
              data-testid="input-contact-message"
            />
          </div>

          <div className="flex gap-3 pt-2">
            <Button
              type="submit"
              disabled={isSubmitting}
              className="flex-1"
              data-testid="button-submit-contact"
            >
              <Send className="w-4 h-4 mr-2" />
              {isSubmitting ? 'Sending...' : 'Send Message'}
            </Button>
            <Button
              type="button"
              variant="outline"
              onClick={() => onOpenChange(false)}
              data-testid="button-cancel-contact"
            >
              Cancel
            </Button>
          </div>
        </form>

        <div className="border-t pt-4 mt-4 space-y-3">
          <h4 className="font-semibold text-sm">Other Ways to Reach Us:</h4>
          <div className="space-y-2 text-sm">
            <div className="flex items-center gap-2 text-muted-foreground">
              <Mail className="w-4 h-4" />
              <span>info@afribet.com</span>
            </div>
            <div className="flex items-center gap-2 text-muted-foreground">
              <Phone className="w-4 h-4" />
              <span>+234 800 AFRIBET</span>
            </div>
            <div className="flex items-center gap-2 text-muted-foreground">
              <MapPin className="w-4 h-4" />
              <span>Lagos, Nigeria</span>
            </div>
          </div>
        </div>
      </DialogContent>
    </Dialog>
  );
}

api/index.ts.content:
import express from "express";
import type { VercelRequest, VercelResponse } from '@vercel/node';
import { registerRoutes } from "../server/routes";

const app = express();

app.use(express.json());
app.use(express.urlencoded({ extended: false }));

let routesRegistered = false;

async function ensureRoutes() {
  if (!routesRegistered) {
    await registerRoutes(app);
    routesRegistered = true;
  }
}

export default async function handler(req: VercelRequest, res: VercelResponse) {
  await ensureRoutes();

  return new Promise((resolve, reject) => {
    app(req as any, res as any, (err: any) => {
      if (err) {
        reject(err);
      } else {
        resolve(undefined);
      }
    });
  });
}
